mapper.select:
select news_id as id,news_title as title,news_pubTime as pubTime,news_url as url from tbl_news where news_type = #{type}
and (news_title like '%${keyword}%' or news_pubTime like '%${keyword}%')
order by news_pubTime desc limit #{start},#{num};

select news_title as title,news_pubTime as pubTime,news_content as content
from tbl_news
where news_id = #{newsID};

select count(*) from tbl_news

	
		news_type = #{type}
	
	
		and news_pubTime like '${pubTime}%'
	
	
		and (news_title like '%${keyword}%' or news_pubTime like '%${keyword}%')
	

;
