mapper.select:
select a.Answ_Id,a.Answ_Adpotion,a.Answ_Content ,a.Answ_Time,a.Answ_Agreement_Num,a.Answ_floor ,u.User_Username,u.User_Icon_Url ,an.Agre_Id as hasAgreement from tbl_answer as a left join tbl_user as u on u.user_id = a.Answ_User_Id left join tbl_ansagreement as an on a.Answ_Id = an.Agre_Ans_Id
a.Answ_Question_Id = #{questionId}
and an.Agre_User_Id = #{userId}
order by a.Answ_Time desc limit #{start},#{num};

select count(*) from tbl_ansagreement
	where Agre_User_Id = #{userId}
		and Agre_Ans_Id = #{answerId};
